import './App.css';
import ArticleList from './component/ArticleList';
import { useState, useEffect } from 'react';
import Form from './component/Form';

function App() {
  const [articles, setArticles] = useState([])
  const [editedArticle, setEditedArticle] = useState(null)
  useEffect(() => {
    fetch('http://127.0.0.1:5000/get', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json'
      }
    })
      .then(resp => resp.json())
      .then(resp => setArticles(resp))
      .catch(error => console.log(error));
  }, [])

  const editArticle = (article) => {
    setEditedArticle(article)
  }

  const updateData = (article) => {
    const new_article = articles.map(my_article => {
      if (my_article.id === article.id) {
        return article
      } else {
        return my_article
      }
    })
    setEditedArticle(null)
    setArticles(new_article)
  }

  const openForm = () => {
    setEditedArticle({ title: '', body: '' })
  }

  return (
    <div className="App">

      <h1>Flask and React Test</h1>

      <button className='btn btn-success'
        onClick={openForm}>
        Add
      </button>
      <ArticleList articles={articles} editArticle={editArticle} />
      {editedArticle ? <Form editedArticle={editedArticle} updateData={updateData} /> : null}
    </div>
  );
}

export default App;
