import React, { useState } from 'react'

function InsertForm({ insertedArticle, updateData }) {
    const [insertTitle, setInsertTitle] = useState(insertedArticle.title)
    const [insertBody, setInsertBody] = useState(insertedArticle.body)
    const insertArticle = () => {
        APIService.InsertArticle({ insertTitle, insertBody })
    }
    return (
        <div>
            {
                insertedArticle ? (
                    <div className="mb-3">
                        <label htmlform="title" className='form-label'>Title</label>
                        <input type="text" className='form-control' value={insertTitle} placeholder="please enter title" onChange={(e) => setInsertTitle(e.target.value)} />
                        <label htmlform="body" className='form-label'>Article</label>
                        <textarea className='form-control' value={insertBody} placeholder="please enter article" onChange={(e) => setInsertBody(e.target.value)} />
                        <button className='btn btn-success mt-3' onClick={insertArticle}>Add</button>
                    </div>
                ) : null
            }
        </div>
    )
}

export default InsertForm
