import React from 'react'
import { useState } from 'react'
import APIService from './APIServices'

function UpdateForm({ editedArticle, updateData }) {
    const [editTitle, setEditTitle] = useState(editedArticle.title)
    const [editBody, setEditBody] = useState(editedArticle.body)
    const updateArticle = () => {
        APIService.UpdateArticle(editedArticle.id, { editTitle, editBody })
            .then(resp => updateData(resp))
            .catch(error => console.log(error))
    }
    return (
        <div>
            {
                editedArticle ? (
                    <div className="mb-3">
                        <label htmlform="title" className='form-label'>Title</label>
                        <input type="text" className='form-control' value={editTitle} placeholder="please enter title" onChange={(e) => setEditTitle(e.target.value)} />
                        <label htmlform="body" className='form-label'>Article</label>
                        <textarea className='form-control' value={editBody} placeholder="please enter article" onChange={(e) => setEditBody(e.target.value)} />
                        <button className='btn btn-success mt-3' onClick={updateArticle}>Edit</button>
                    </div>
                ) : null
            }
        </div>
    )
}

export default UpdateForm
