import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';
import cors from 'cors';

const root = ReactDOM.createRoot(document.getElementById('root'));
const corsOptions = {
  origin: 'http://localhost:3000',  // 指定允许的来源（你的React应用程序的地址）
  credentials: true  // 允许发送跨域凭证（例如Cookie）
};
App.use(cors(corsOptions));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);
